scriptlinks.script:
				(function(){				
					function onload(){				
						var ln =  utils.cookies.get("loginedName");
						if(ln){
							//console.log("logined");
							var login = utils.getEleById("login");
							login.innerHTML = "
用户"+ln+"已登录";

						}else{
							//console.log("onload");
							var ele = utils.getEleById("login_link");
							utils.addEvent(ele,"click",user.onLogin);
							ele = utils.getEleById("register_link");
							utils.addEvent(ele,"click",user.onRegister);
						}
					}
					utils.addEvent(window,"load",onload);
					console.log("addEvent");
				})();
